INSERT INTO public.jabatan (id, kode_jabatan, nama_jabatan, ikhtisar_jabatan, kelas_jabatan, prestasi_diharapkan, slug, struktur_id, created_at, updated_at) VALUES ('3acd6dd1-bc6f-44c3-91c5-741d28c5e406', '----', 'KEPALA BAGIAN ADMINISTRASI KEANGGOTAAN DAN KEPEGAWAIAN', 'Memimpin Bagian Administrasi Keanggotaan dan Kepegawaian dalam rangka melaksanakan Penyiapan bahan koordinasi, bahan  perumusan  kebijakan, dan pelaksanaan dukungan di bidang administrasi keanggotaan dan kepegawaian di lingkungan Sekretariat Jenderal DPD RI, meliputi penyiapan bahan/ materi pelaksanaan keadministrasian Anggota, pengangkatan dan pelantikan Anggota, pengganti antar waktu, pensiun Anggota, fasilitasi naskah dinas kegiatan Anggota Dewan, serta pelaksanaan keadministrasian pegawai, pangkat dan jabatan, promosi, mutasi, penilaian kinerja, disiplin, pemberhentian, pensiun, kegiatan konseling dan penyelesaian masalah kepegawaian, penerapan budaya kerja, penghargaan Anggota dan pegawai, jaminan pensiun dan jaminan hari tua, perlindungan, fasilitasi pelayanan kesehatan dan kesejahteraan Anggota dan pegawai, pengelolaan ketatausahaan Biro Organisasi, Keanggotaan, dan Kepegawaian, sesuai dengan peraturan dan ketentuan yang berlaku, agar pekerjaan di Bagian Administrasi Keanggotaan dan Kepegawaian berjalan dengan baik dan lancar sehingga visi dan misi serta tujuan Sekretariat Jenderal DPD RI tercapai.', '12', 'Baik', 'okk-akk', 'b1604a5b-f200-497e-8454-b84e86d7da8e', '2025-09-20 16:30:41.586506+07', '2025-09-20 16:31:02.507426+07');
INSERT INTO public.jabatan (id, kode_jabatan, nama_jabatan, ikhtisar_jabatan, kelas_jabatan, prestasi_diharapkan, slug, struktur_id, created_at, updated_at) VALUES ('f6453c15-36c6-44b8-a956-2a048435e881', '----', 'KEPALA BAGIAN PENGEMBANGAN SUMBER DAYA MANUSIA', 'Memimpin Bagian Pengembangan Sumber Daya Manusia dalam rangka melaksanakan penyiapan bahan koordinasi, bahan perumusan  kebijakan, dan pelaksanaan dukungan di bidang penyusunan formasi, pengadaan, penataan, pola karier, pengembangan kompetensi pegawai, analisis kebutuhan, perencanaan, monitoring dan evaluasi pendidikan dan pelatihan, rekomendasi pemberian beasiswa, tugas belajar dan ijin belajar, pengelolaan kerja sama dalam dan luar negeri di bidang pengembangan Sumber Daya Manusia serta penyusunan kebijakan, pengelolaan data, penilaian angka kredit, pengembangan dan penyelesaian masalah di bidang jabatan fungsional di lingkungan Sekretariat Jenderal DPD RI, sesuai dengan peraturan dan ketentuan yang berlaku, agar pekerjaan di Bagian Pengembangan Sumber Daya Manusia berjalan dengan baik dan lancar sehingga visi dan misi serta tujuan Sekretariat Jenderal DPD RI tercapai.', '12', 'Baik', 'okk-psdm', 'c27eb9c6-92fc-4eec-b1d0-93d6362c8301', '2025-09-20 16:48:00.070038+07', '2025-09-20 16:48:25.271799+07');
